import React, { Component } from 'react';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { Grid, Menu } from 'semantic-ui-react';

import './App.css';

import curriculums from './data/curriculums';
import disciplines from './data/disciplines';
import estimates from './data/estimates';

import AboutUGVA from './components/AboutUGVA/About_UGVA'
import Curriculums from './components/Curriculums/Curriculums';

class App extends Component {
  state = { activeItem: '' };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;

    return (
      <BrowserRouter>
        <Grid>
          <Grid.Column width={4}>
            <Menu fluid vertical tabular>
              <Menu.Item
                name='О МЕТОДЕ UGVA'
                active={activeItem === 'О МЕТОДЕ UGVA'}
                onClick={this.handleItemClick}
                href="/aboutugva"
              />
              <Menu.Item
                name='УЧЕБНЫЕ ПЛАНЫ'
                active={activeItem === 'УЧЕБНЫЕ ПЛАНЫ'}
                onClick={this.handleItemClick}
                href="/curriculums"
              />
              <Menu.Item
                name='СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'
                active={activeItem === 'СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'}
                onClick={this.handleItemClick}
                href="/freevisualization"
              />
              <Menu.Item
                name='Figushkins'
                active={activeItem === 'Figushkins'}
                onClick={this.handleItemClick}
              />
            </Menu>
          </Grid.Column>

          <Grid.Column stretched width={5}>
            <div className='app_content'>
              <Routes>
                <Route exact path="/" element={<AboutUGVA />} />
                <Route exact path="/aboutugva" element={<AboutUGVA />} />
                <Route exact path="/curriculums" element={<Curriculums disciplines = {disciplines}
                                                                        estimates = {estimates} 
                                                                        curriculums = {curriculums}/>} />
                {/* <Route exact path="/visualization" element={Visualization} /> */}
              </Routes>
            </div>

          </Grid.Column>
        </Grid>
      </BrowserRouter>
    )
  }
}

export default App;
