import React from "react";
import { useParams } from "react-router-dom";

import './Curriculums_review.css';

const Curriculums_review = (props) => {
    
    let { id } = useParams();
    
    const arrayCurriculums = props.curriculums;
    const arrayDisciplines = props.disciplines;
    const skills = props.skills;
    const estimates = props.estimates;

    let ourCurriculum = [];
    let disciplinesOurCurriculum = [];
    let totalHours = 0;

    arrayCurriculums.forEach(el => {
        if (el.id == id) ourCurriculum = el;
    });

    arrayDisciplines.forEach(el => {
        if (el.curriculum_id == id) disciplinesOurCurriculum.push(el);
    })

    console.log(ourCurriculum);
    console.log(disciplinesOurCurriculum);

    const disciplineTable = disciplinesOurCurriculum.map( el => {
        totalHours += el.audit_hours;
        return(
            <tr key={el.id} border='1'>
                <td>{el.id}</td>
                <td>{el.name}</td>
                <td>{el.audit_hours}</td>
                <td>{el.part_id}</td>
                <td>{el.control_id}</td>
            </tr>
        )
    })

    const skillsHeader = skills.map(skill => {
        return <th>{skill.name}</th>
    })

    const estimatesTable = disciplinesOurCurriculum.map (el => {
        const disciplineEstimates = skills.map(skill => {
            let estimate = 0;
            estimates.forEach(est => {
                if (est.discipline_id == el.id && est.skill_id == skill.id) estimate = est.estimate;
            })
            return <td>{estimate}</td>
        })

        return (
            <tr key={el.id} border='1'>
                <td>{el.name}</td>
                {disciplineEstimates}
            </tr>
        )
    })

    const sumEstimates = skills.map(skill => {
        let sum = 0;
        disciplinesOurCurriculum.forEach(el =>{
            estimates.forEach(est => {
                if (est.discipline_id == el.id && est.skill_id == skill.id) sum += est.estimate;
            })
        })
        return <td>{sum}</td>
    })

    
    
    return (
        <div className="app_content_table">
            <h2 className="header">Информация об учебном плане "{ourCurriculum.name}"</h2>
            <div>
                <ul className="curriculums-review_list">
                    <li>Полное наименвание: "{ourCurriculum.full_name}"</li>
                    <li>Специальность: "{ourCurriculum.speciality_id}"</li>
                    <li>Год начала обучения: {ourCurriculum.year} г.</li>
                    <li>Университет: "{ourCurriculum.university_id}"</li>
                    <li>Ступень: "{ourCurriculum.degree_id}"</li>
                </ul>
                <article className="curriculums-review_article">
                    <h3 className="curriculums-review_article_header">Об учебном плане</h3>
                    <p className="curriculums-review_article_text">{ourCurriculum.about}</p>
                </article>
            </div>

            <div>
                <table className="app_table curriculums-review_table">
                    <thead>
                        <tr border='1'>
                            <th>id</th>
                            <th>name</th>
                            <th>audit_hours</th>
                            <th>Часть</th>
                            <th>форма контроля</th>
                        </tr>
                    </thead>
                    <tbody>
                        {disciplineTable}
                    </tbody>
                    <tfoot>
                        <tr>
                            <td colspan="2">Итого</td>
                            <td>0</td>
                            <td>0</td>
                            <td>0</td>
                        </tr>
                    </tfoot>
            </table> 
            </div>

            <div>   
                <table className="app_table app_table_small curriculums-review_table">
                    <thead>
                        <tr>
                            <th>Дисциплина</th>
                            {skillsHeader}
                        </tr>
                    </thead>
                    <tbody>
                        {estimatesTable}
                    </tbody>
                    <tfoot>
                        <tr>
                            <td>Сумма</td>
                            {sumEstimates}
                        </tr>
                    </tfoot>
                </table>
            </div>
            <br/>            <br/>            <br/>
        </div>
    )
}

export default Curriculums_review;
