import "./Curriculums_visualization.css"
import { useParams } from "react-router-dom";


const Above_curriculums_visualization = (props) => {

    const { id } = useParams();
    console.log(props);

    const { skills, estimates, curriculums, disciplines } = props;

    let ourCurriculum = [];
    let disciplinesOurCurriculum = [];

    curriculums.forEach(el => {
        if (el.id == id) ourCurriculum = el;
    });

    disciplines.forEach(el => {
        if (el.curriculum_id == id) disciplinesOurCurriculum.push(el);
    })

    console.log(disciplinesOurCurriculum);

    let totalHoursDisciplinesOurCurriculum = 0;
    disciplinesOurCurriculum.forEach(el => {
        totalHoursDisciplinesOurCurriculum += el.audit_hours;
    })

    const sumEstimates = skills.map(skill => {
        let sum_base = 0;
        let sum_variable = 0;
        let sum_hours = 0;
        disciplinesOurCurriculum.forEach(el =>{
            sum_hours += el.audit_hours;
            estimates.forEach(est => {
                if (est.discipline_id == el.id && est.skill_id == skill.id) {
                    if (el.part_id == 0) sum_base += est.estimate;
                    else sum_variable += est.estimate;
                }
            })
        })
        return (
            {
                id: skill.id,
                sum_base: sum_base,
                sum_variable: sum_variable,
                sum_hours: sum_hours
            }     
        )
    })

    console.log(sumEstimates);
    console.log(totalHoursDisciplinesOurCurriculum);


    return (
        <div>d</div>
    )
}

export default Above_curriculums_visualization;
