import React from "react";
import { useParams } from "react-router-dom";

const Curriculums_review = (props) => {
    
    let { id } = useParams();
    console.log(props)
    const arrayCurriculums = props.curriculums;
    const arrayDisciplines = props.disciplines;

    let ourCurriculum = [];
    let disciplinesOurCurriculum = [];
    let totalHours = 0;

    arrayCurriculums.forEach(el => {
        if (el.id == id) ourCurriculum = el;
    });

    arrayDisciplines.forEach(el => {
        if (el.curriculum_id == id) disciplinesOurCurriculum.push(el);
    })

    console.log(ourCurriculum);
    console.log(disciplinesOurCurriculum);

    const disciplineTable = disciplinesOurCurriculum.map( el => {
        totalHours += el.audit_hours;
        return(
            <tr key={el.id} border='1'>
                <td>{el.id}</td>
                <td>{el.name}</td>
                <td>{el.audit_hours}</td>
                <td>{el.part_id}</td>
                <td>{el.control_id}</td>
            </tr>
        )
    })


    
    
    return (
        <div className="app_content_visualization">
            <h2 className="header">Информация об учебном плане "{ourCurriculum.name}"</h2>
            <div>
                <ul className="curriculums-review_list">
                    <li>Полное наименвание: "{ourCurriculum.full_name}"</li>
                    <li>Специальность: "{ourCurriculum.speciality_id}"</li>
                    <li>Год начала обучения: {ourCurriculum.year} г.</li>
                    <li>Университет: "{ourCurriculum.university_id}"</li>
                    <li>Ступень: "{ourCurriculum.degree_id}"</li>
                </ul>
                <article className="curriculums-review_article">
                    <h3>Об учебном плане</h3>
                    <p>{ourCurriculum.about}</p>
                </article>
            </div>
            <div>
                {disciplineTable}
            </div>
            <br/>            <br/>            <br/>
        </div>
    )
}

export default Curriculums_review;
