import React from 'react';
import { Icon } from 'semantic-ui-react';

import '../Curriculums_review.css';

class Disciplines_table extends React.Component {
    constructor(props) {
        super(props);
        this.state = { showElementMeaning: false };
    
        this.onShowElementClick = this.onShowElementClick.bind(this);
    }

    onShowElementClick = (prevState) => {
        this.setState(prevState => ({
            showElementMeaning: !prevState.showElementMeaning
        }))
    }

    render(){
        const { disciplinesOurCurriculum } = this.props;
        let totalHours = 0;
        
        const disciplineTable = disciplinesOurCurriculum.map( el => {
            totalHours += el.audit_hours;
            return(
                <tr key={el.id} border='1'>
                    <td>{el.id}</td>
                    <td>{el.name}</td>
                    <td>{el.part_id}</td>
                    <td>{el.control_id}</td>
                    <td>{el.audit_hours}</td>
                </tr>
            )
        })

        return (
            <div>
                <Icon name='caret square down outline'>Свернуть</Icon>
                <div className="curriculum-review_hidden-content">
                    <button className="curriculum-review_hidden-content_btn" onClick={this.onShowElementClick}>
                        {this.state.showElementMeaning 
                            ?(<Icon name='caret square down outline'/>, 'Свернуть')
                            :<Icon name='caret square down'>Развернуть</Icon>}
                    </button>
                    <p>Перечень дисциплин</p>
                </div>
                <hr />


                <div className={this.state.showElementMeaning ? "" : "curriculum-review_hidden"}>
                    <table className="app_table curriculums-review_table">
                        <thead>
                            <tr border='1'>
                                <th>id</th>
                                <th>name</th>
                                <th>Часть</th>
                                <th>форма контроля</th>
                                <th>audit_hours</th>
                            </tr>
                        </thead>
                        <tbody>
                            {disciplineTable}
                        </tbody>
                        <tfoot>
                            <tr>
                                <td colSpan="4">Итого</td>
                                <td>{totalHours}</td>
                            </tr>
                        </tfoot>
                    </table>
                </div> 
            </div>
        )
    }
}

export default Disciplines_table;
