import "./Curriculums_visualization.css"
import { useParams } from "react-router-dom";


const Above_curriculums_visualization = (props) => {

    const { id } = useParams();
    console.log(props);

    const { skills, estimates, curriculums, disciplines } = props;

    let ourCurriculum = [];
    let disciplinesOurCurriculum = [];

    curriculums.forEach(el => {
        if (el.id == id) ourCurriculum = el;
    });

    disciplines.forEach(el => {
        if (el.curriculum_id == id) disciplinesOurCurriculum.push(el);
    })

    console.log(disciplinesOurCurriculum);



    const sumEstimatesBase = skills.map(skill => {
        let sum = 0;
        disciplinesOurCurriculum.forEach(el =>{
            estimates.forEach(est => {
                if (est.discipline_id == el.id && est.skill_id == skill.id && el.part_id == 0) sum += est.estimate;
            })
        })
        // skill.sum = sum;


        return (
            {
                sum: sum,
                id: skill.id
            }     

        )
        
    })

    console.log(sumEstimatesBase);


    return (
        <div>d</div>
    )
}

export default Above_curriculums_visualization;
