import React, { Component } from 'react';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { Grid, Menu } from 'semantic-ui-react';

import './App.css';

import curriculums from './data/curriculums';
import disciplines from './data/disciplines';
import estimates from './data/estimates';
import dataForVisualization from './data/dataForVisualization';

import AboutUGVA from './components/AboutUGVA/About_UGVA'
import Curriculums from './components/Curriculums/Curriculums';
import Visualization from './components/Visualization/Visualization';

class App extends Component {
  state = { activeItem: '' };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;
    // console.log(dataForVisualization)
    return (
      <BrowserRouter>
      <div className='app_components'>
          <div className='app_menu'>
            <Menu fluid vertical tabula>
              <Menu.Item
                name='О МЕТОДЕ UGVA'
                active={activeItem === 'О МЕТОДЕ UGVA'}
                onClick={this.handleItemClick}
                href="/aboutugva"
              />
              <Menu.Item
                name='УЧЕБНЫЕ ПЛАНЫ'
                active={activeItem === 'УЧЕБНЫЕ ПЛАНЫ'}
                onClick={this.handleItemClick}
                href="/curriculums"
              />
              <Menu.Item
                name='СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'
                active={activeItem === 'СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'}
                onClick={this.handleItemClick}
                href="/freevisualization"
              />
              <Menu.Item
                name='Figushkins'
                active={activeItem === 'Figushkins'}
                onClick={this.handleItemClick}
              />
            </Menu>
          </div>

            <div className="app_content common_content">
              <Routes>
                <Route exact path="/" element={
                <AboutUGVA/>} />
                <Route exact path="/aboutugva" element={<AboutUGVA />} />
                <Route exact path="/curriculums" element={<Curriculums disciplines = {disciplines}
                                                                        estimates = {estimates} 
                                                                        curriculums = {curriculums}/>} />
                <Route exact path="/freevisualization" element={<Visualization {...dataForVisualization}/>} />
              </Routes>
            </div>
        </div>
      </BrowserRouter>
    )
  }
}

export default App;
