import React from 'react';
import { Icon } from 'semantic-ui-react';

import '../Curriculums_review.css';

class Estimates_table extends React.Component {
    constructor(props) {
        super(props);
        this.state = { showElementMeaning: false };
    
        this.onShowElementClick = this.onShowElementClick.bind(this);
    }

    onShowElementClick = (prevState) => {
        this.setState(prevState => ({
            showElementMeaning: !prevState.showElementMeaning
        }))
    }

    render(){
        const { skills, estimates, disciplinesOurCurriculum } = this.props;

        const skillsHeader = skills.map(skill => {
            return <th key={skill.id}>{skill.name}</th>
        })

        const estimatesTable = disciplinesOurCurriculum.map (el => {
            const disciplineEstimates = skills.map(skill => {
                let estimate = 0;
                estimates.forEach(est => {
                    if (est.discipline_id == el.id && est.skill_id == skill.id) estimate = est.estimate;
                })
                return <td key={skill.id}>{estimate}</td>
            })

            return (
                <tr key={el.id} border='1'>
                    <td>{el.name}</td>
                    {disciplineEstimates}
                </tr>
            )
        })

        const sumEstimates = skills.map(skill => {
            let sum = 0;
            disciplinesOurCurriculum.forEach(el =>{
                estimates.forEach(est => {
                    if (est.discipline_id == el.id && est.skill_id == skill.id) sum += est.estimate;
                })
            })
            return <td key={skill.id}>{sum}</td>
        })
    
        return (
            <div>
                <div className="curriculum-review_hidden-content">
                    <button className="curriculum-review_hidden-content_btn" onClick={this.onShowElementClick}>
                        {this.state.showElementMeaning 
                            ? <Icon name='caret up' />
                            : <Icon name='caret down' />}
                        {this.state.showElementMeaning ? 'Свернуть' : 'Развернуть'}
                    </button>
                    <p className="curriculum-review_hidden-content_p">Перечень оценок выделенных умений</p>
                </div>
                <hr className='curriculum-review_hr' />

                <div className={this.state.showElementMeaning ? "" : "curriculum-review_hidden"}>
                    <table className="app_table app_table_small curriculums-review_table">
                        <thead>
                            <tr>
                                <th>Дисциплина</th>
                                {skillsHeader}
                            </tr>
                        </thead>
                        <tbody>
                            {estimatesTable}
                        </tbody>
                        <tfoot>
                            <tr>
                                <td>Сумма</td>
                                {sumEstimates}
                            </tr>
                        </tfoot>
                    </table>
                </div>
            </div>
        )
    }
}

export default Estimates_table;
