import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { Grid, Menu, Segment } from 'semantic-ui-react';

import './App.css';

import curriculums from './data/curriculums';
import disciplines from './data/disciplines';
import estimates from './data/estimates';

import AboutUGVA from './components/AboutUGVA/About_UGVA'
import Curriculims from './components/Curriculums/Curriculums';

// import Visualization from './components/Visualization';

// import CurriculumsList from './components/Curriculums/CurriculumsList/CurriculumsList';

class App extends Component {
  state = { activeItem: 'О МЕТОДЕ UGVA' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <BrowserRouter>
        <Grid class="container">
          <Grid.Column width={4}>
            <Menu fluid vertical tabular>
              <Menu.Item
                name='О МЕТОДЕ UGVA'
                active={activeItem === 'О МЕТОДЕ UGVA'}
                onClick={this.handleItemClick}
                href="/aboutUgva"
              />
              <Menu.Item
                name='УЧЕБНЫЕ ПЛАНЫ'
                active={activeItem === 'УЧЕБНЫЕ ПЛАНЫ'}
                onClick={this.handleItemClick}
                href="/curriculums"
              />
              <Menu.Item
                name='СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'
                active={activeItem === 'СВОБОДНАЯ ВИЗУАЛИЗАЦИЯ'}
                onClick={this.handleItemClick}
                href="/freeVisualization"
              />
              <Menu.Item
                name='Figushkins'
                active={activeItem === 'Figushkins'}
                onClick={this.handleItemClick}
              />
            </Menu>
          </Grid.Column>

          <Grid.Column stretched width={12}>
            <Switch>
              <Route exact path="/aboutUGVA" component={AboutUGVA} />
              <Route exact path="/curriculums" component={Curriculums} disciplines = {disciplines}
                                                                      estimates = {estimates} />
              {/* <Route exact path="/visualization" component={Visualization} /> */}
            </Switch>

          </Grid.Column>
        </Grid>
      </BrowserRouter>
    )
  }
}

export default App;
